<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class Meja extends CI_Controller{	function __Construct(){		parent::__Construct();		$this->load->library('pagination');		$this->load->Model('Model_meja');		$this->load->helper('form');	}		function index(){		$config = array();        $config["base_url"] = base_url() . "index.php/meja/page";        $config["total_rows"] = $this->Model_meja->CountData();        $config["per_page"] = 50;        $config["uri_segment"] = 3;		$config['first_link'] = 'Pertama';		$config['last_link'] = 'Terakhir';        $this->pagination->initialize($config);        $page = ($this->uri->segment(3)) ? $this->uri->segment(3) : 0;		$data = array(			'title'=>'Data Meja',			'data'=>$this->Model_meja->ReadData($config["per_page"], $page),			'links'=>$this->pagination->create_links(),			'page'=>$page,			'totaldata'=>$this->Model_meja->CountData()		);		$this->load->view('view_meja',$data);	}		function page(){		$config = array();        $config["base_url"] = base_url() . "index.php/meja/page";        $config["total_rows"] = $this->Model_meja->CountData();        $config["per_page"] = 50;        $config["uri_segment"] = 3;		$config['first_link'] = 'Pertama';		$config['last_link'] = 'Terakhir';        $this->pagination->initialize($config);        $page = ($this->uri->segment(3)) ? $this->uri->segment(3) : 0;		$data = array(			'title'=>'Data Meja',			'data'=>$this->Model_meja->ReadData($config["per_page"], $page),			'links'=>$this->pagination->create_links(),			'page'=>$page,			'totaldata'=>$this->Model_meja->CountData()		);		$this->load->view('view_meja',$data);	}		function Save(){		$data['no_meja'] = $this->input->post('no_meja');		$data['booking'] = 'n';		$data['tanggal'] = '0000-00-00';		$data['waktu'] = '00:00';		$data['tamu'] = '';		$data['kapasitas'] = $this->input->post('kapasitas');		$this->Model_meja->Save($data);	}		function Edit(){		$id = $this->input->get('id');		$data = $this->Model_meja->Edit($id);		echo $json_encode = json_encode($data);	}		function Update(){		$id = $this->input->post('no_meja');		$data['kapasitas'] = $this->input->post('kapasitas');		$this->Model_meja->Update($data,$id);	}		function Delete(){		$id = $this->input->get('id');		$this->Model_meja->Delete($id);	}	}