<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class Model_meja extends CI_Model{		function __Construct(){		parent::__Construct();		$this->load->database();	}		function CountData(){		 return $this->db->count_all('meja');	}		function ReadData($limit, $start) {        $this->db->limit($limit, $start);		$this->db->order_by("no_meja", "asc");        $query = $this->db->get('meja');        if ($query->num_rows() > 0) {            foreach ($query->result() as $row) {                $data[] = $row;            }            return $data;        }        return false;   }         function Save($data){		$this->db->insert('meja',$data);   }      function Edit($no_meja){		$data = array();		$this->db->where('no_meja',$no_meja);		$query = $this->db->get('meja');		if($query->num_rows()>0){			foreach($query->result_array() as $row){				$data[] = $row;			}		}		$query->free_result();		return $data;	}   	function Update($data,$no_meja){		$this->db->where('no_meja',$no_meja);		$this->db->update('meja',$data);	}   	function Delete($no_meja){		$this->db->where('no_meja',$no_meja);			$this->db->delete('meja');		$data = $this->CountData();		if($data==0){			$this->db->query('TRUNCATE meja');		}	}		function ListPencarian(){		$data = array();		$query = $this->db->query("DESC meja");		if($query->num_rows()>0){			foreach($query->result_array() as $row){				$data[] = $row;			}		}		$query->free_result();		return $data;	}		function SearchData($limit, $start,$kategori,$value) {        $this->db->limit($limit, $start);		$this->db->like($kategori, $value);        $query = $this->db->get('meja');        if ($query->num_rows() > 0) {            foreach ($query->result() as $row) {                $data[] = $row;            }            return $data;        }        return false;   }}